import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css'; 
import React, { useState } from 'react';
import SongList, { displaySongs } from "./components/SongList"

// import 'bootstrap/dist/css/bootstrap.min.css'; // This imports bootstrap css styles. You can use bootstrap or your own classes by using the className attribute in your elements.

// Menu data. An array of objects where each object represents a menu item. Each menu item has an id, title, description, image name, and price.
// You can use the image name to get the image from the images folder.
const menuItems = [
  {

  }
];

const getToken = async () => {
  const res = await fetch("https://accounts.spotify.com/api/token", {
    method: 'POST',
    headers: {
      'Content-Type': "application/x-www-form-urlencoded",
    },
    body: 'grant_type=client_credentials&client_id=cc1d224045bf4a3ab0705f436c1dde4f&client_secret=7fd247aa39a84791800daa5753a500a7'
  });
  const token = await res.json();
  return token.access_token;
}

const accToken = getToken();

const App = () => {

  const [search, setSearch] = useState("");

  const searchSongs = () => {
    return displaySongs(search, accToken);
  }

  return (
    <div>
      <div class='container'>
        <div class="row justify-content-center">
          <button type="button" class="menu-button" onClick={() => displaySongs("Bastille", accToken)}>
            Bastille
          </button>
        </div>
        <div class='row justify-content-center'>
          <button type="button" class="menu-button" onClick={() => displaySongs("Kendrick Lamar", accToken)}>
            Kendrick Lamar
          </button>
        </div>
        <div class='row justify-content-center'>
          <button type="button" class="menu-button" onClick={() => displaySongs("Taylor Swift", accToken)}>
            Taylor Swift
          </button>
        </div>
        <div class="row justify-content-center">
          <div class="col-4">
            <input 
              width="80px"
              type="text"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              placeholder="Search an artist"
            />
          </div>
          <div class="col-4">
            <button 
              onClick={searchSongs}
            >
              Search
            </button>
          </div>
        </div>
      </div>
      
    </div>
  );
}

export default App;
