    return (
        <div>
            {/* {songs.map(item => (
                <SongDisplay key={item.id} item={item}/>
            ))} */}
        </div>
    );
}

export const displayWeather = async (name) => {
    const res = getCity(name);
    if (!res) {
        return;
    }
    console.log("have result: " + {res});
    try { 
        const wthrres = await fetch('https://geocoding-api.open-meteo.com/v1/forecast?latitude=${res.latitude}&longitude=${res.longitude}&temperature_unit=fahrenheit&timezone=CST&');
        const weather = await wthrres.json();

    } catch(error) {
        alert("Error in finding city " + {name}.name);
    }

} 

const getCity = async (name) => {
    console.log({name}.name);
    console.log('https://geocoding-api.open-meteo.com/v1/search?name=' + {name}.name);
    try {
        const res = await fetch('https://geocoding-api.open-meteo.com/v1/search?name=' + {name}.name);
        const results = await res.json();
        console.log(results.results[0]);
        if (results.results && results.results.length > 0) {
            return results.results[0]
        } else {
            alert("Could not find city " + {name}.name);
            return null;
        }
    } catch (error) {
        alert("Error in finding city " + {name}.name);
    }
}
