import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css'; 
import React, { useState } from 'react';
import SongList, { displayWeather } from "./components/WeatherList"
import { fetchWeatherApi } from 'openmeteo';

// import 'bootstrap/dist/css/bootstrap.min.css'; // This imports bootstrap css styles. You can use bootstrap or your own classes by using the className attribute in your elements.

// Menu data. An array of objects where each object represents a menu item. Each menu item has an id, title, description, image name, and price.
// You can use the image name to get the image from the images folder.
const menuItems = [
  {

  }
];




const App = () => {

  const [search, setSearch] = useState("");
  const [weather, setWeather] = useState([]);


  const searchCity = () => {
    return getCity(search);
  }

  const getCity = async (name) => {
    console.log({name}.name);
    console.log('https://geocoding-api.open-meteo.com/v1/search?name=' + {name}.name);
    try {
        const res = await fetch('https://geocoding-api.open-meteo.com/v1/search?name=' + {name}.name);
        const results = await res.json();
        console.log(results.results[0]);
        if (results.results && results.results.length > 0) {
	
          const params = {
            "latitude": results.results[0].latitude,
            "longitude": results.results[0].latitude,
            "daily": "sunset",
            "timezone": "America/Chicago",
            "forecast_days": 10,
            "temperature_unit": "fahrenheit"
          };
          const url = "https://api.open-meteo.com/v1/forecast";
          const responses = await fetchWeatherApi(url, params);
          

          
          // Process first location. Add a for-loop for multiple locations or weather models
          const response = responses[0];
          
          // Attributes for timezone and location
          const utcOffsetSeconds = response.utcOffsetSeconds();
          const timezone = response.timezone();
          const timezoneAbbreviation = response.timezoneAbbreviation();
          const latitude = response.latitude();
          const longitude = response.longitude();
          
          const daily = response.daily();
          
          console.log(response.daily);
          console.log(Number(daily.time()));
          console.log(Number(daily.timeEnd()))
          // Note: The order of weather variables in the URL query and the indices below need to match!
          const weatherData = {

					  daily: {
		          time: range(Number(daily.time()), Number(daily.timeEnd()), daily.interval()).map(
			          (t) => new Date((t + utcOffsetSeconds) * 1000)
		          ),
		          sunset: daily.variables(0).valuesArray(),
	          },

          };

        } else {
            alert("Could not find city " + {name}.name);
            return null;
        }
    } catch (error) {
        console.log({error});
        alert("Error in finding city " + {name}.name);
        return null;
    }
  }




  return (
    <div>
      <div class='container'>
        <div class="row justify-content-center">
          <button type="button" class="menu-button" onClick={() => getCity("Austin")}>
            Austin
          </button>
        </div>
        <div class='row justify-content-center'>
          <button type="button" class="menu-button" onClick={() => getCity("Chicago")}>
            Chicago
          </button>
        </div>
        <div class='row justify-content-center'>
          <button type="button" class="menu-button" onClick={() => getCity("Las Vegas")}>
            Las Vegas
          </button>
        </div>
        <div class="row justify-content-center">
          <div class="col-4">
            <input 
              width="80px"
              type="text"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              placeholder="Search an artist"
            />
          </div>
          <div class="col-4">
            <button 
              onClick={searchCity}
            >
              Search
            </button>
          </div>
        </div>
      </div>
      
    </div>
  );




}

export default App;
